import React, {useState} from 'react'
import './index.css' 
function App() {

  const [kwota ,setKwota] =useState(0)
  const [kwotab ,setKwotab]  =useState(0)
  const [kwotac ,setKwotac] =useState(0)
  const [Oplata ,setOplata ] =useState(0)
  const [Message ,setMessage] =useState('')
  let liczonko = (event)=> {
    event.preventDefault()
    if (kwota===0){
      alert('podaj liczbe')
    }else if (kwota>4000) {
       alert("Musisz zaplacic 750zl")
    }else{
    alert('musisz zaplacic 520 ')
    } 
  } 




  return (
    <div className="app">
     <div className="container">
      <h2 className="center">Kalkulator oplat</h2>

      <form onSubmit={liczonko}>
      <div>
        <label>Ilosc sprzedanego Alkoholu kategorii A</label>
        <input value={kwota} onChange={(event) => setKwota(event.target.value)} />
        <button className="btn" type='sumbit'>Oblicz</button>

      </div> 

      </form>
      <div>
        <label>Ilosc sprzedanego Alkoholu kategorii B</label>
        <input value={kwotab} onChange={(event) => setKwotab(event.target.value)} />
  <button className="btn" type='sumbit'>Oblicz</button>

      </div> 
      <div>
        <label>Ilosc sprzedanego Alkoholu kategorii C</label>
        <input value={kwotac} onChange={(event) => setKwotac(event.target.value)} />
  <button className="btn" type='sumbit'>Oblicz</button>


    </div>


<div>




  </div> 





<div className="center">
  <h3>Kwota wynosi: {Oplata} </h3>
  <p>{Message}</p>
</div>




  </div>
  </div>
  );
}

export default App;
