import React from "react";
import ReactDOM from "react-dom";
import useInput from "./logic";

import  useChuj from "./calc"


function App() {
  const [c1, addC1] = useChuj();
 const [nigger,niggerr]=useInput()
const [calc, useCalc]=React.useState()
let calculating = (e)=>{
  e.preventDefault
if(nigger>35000){
  let calc=(nigger*0.014)
  calc(useCalc.toFixed(1))
}else{
  let calc=(nigger-nigger+520)
  calc(useCalc.toFixed(1))
}
}
  return (
    <div className="App">
      <h1>Test useState</h1>
      <h2>Example 1</h2>
      <div>
        <div>{c1}</div>
        <button onClick={addC1}>add c1</button>
      </div>
    <form onSubmit={calculating}>
     <div>
      {niggerr}
     <p> {calc}</p>
      <button className="btn" type='submit'></button>
     </div>
     </form>
      <hr />
      <div>结论：两次调用 hook 分别产生 2 个不同的对象，互不影响。 </div>
    </div>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
