import React,{useState} from 'react'
import './index.css' 
const useQuantitySelector =()=>{
const[cost ,setCosta]=useState();
    
    const onChange =()=>{ 
      if (costa <=37500){
        let Oplata=(costa-costa +520)
        setOplata(Oplata.toFixed(1))
      }else if (costa > 37500) {
         let Oplata=(costa * 0.014)
         setOplata(Oplata.toFixed(1))
      
        }
        else{
      alert('podaj liczbe')
     }
    }
  return{onChange, costa}
}
