import React from "react";
function useInput(opts) {
 

  const [value, setValue] = React.useState('');
  const[calcul,setCalcul]=React.useState()
  if(value<35000){
    let calcul=(value-value+520)
    setCalcul(calcul.toFixed())
   }else {
   let calcul=(value*0.014)
  setCalcul(calcul.toFixed(1))
   }  
  const input = <input value={value}onChange={e => setValue(e.target.value)}{...opts} />
  
    
  return [value, input,calcul];
}


export default useInput;
