import React from "react";
function useInput(opts) {
  const [value, setValue] = React.useState('');
  
  const useInput = () => {
    const [count, setCount] = useState(0);
    const add = () => setCount(count + 1);
  }
  const input = <input
    value={value}
    onChange={e => setValue(e.target.value)}
    {...opts} />

  return [value, input];
}


export default useInput;
