import React from "react";
function useInput(opts) {
  const [value, setValue] = React.useState('');
  if(value>35000){
  alert("musisz zaplacic 520zl")
  }

  
  const input = <input
    value={value}
    onChange={e => setValue(e.target.value)}
    {...opts} />

  return [value, input];
}


export default useInput;
