import React from "react";
function useInput(opts) {
  const [value, setValue] = React.useState('');
  const input = <input value={(value)}onChange={e => setValue(e.target.value)}{...opts} />
   const [calc ,setCalc] = React.useState('')

  if (value > 35700){
    let calc=(value *0.014)
    setCalc(calc.toFixed(1))
    }else if(value<37500) {
      let calc =(value-value + 520)
      setCalc(calc.toFixed)
    }else{
      alert('podaj liczbe')
    }
    


 
return [value, input,calc];
}
export default useInput
