        <button className="btn" type='sumbit'>Oblicz</button>

      </div> 

      </form>
      <form onSubmit={liczenieb}>
      <div>
        <label>Ilosc sprzedanego Alkoholu kategorii B</label>
        <input value={kwotab} onChange={(event2) => setKwotab(event2.target.value)} />
  <button className="btn" type='sumbit'>Oblicz</button>


