    value={value}
    onChange={e => setValue(e.target.value)}
    {...opts} />
    let counting=(e)=> {
      if(value<=35000){
let calcc=(value-value+520)
setCalcc(calcc.toFixed())
    }else if(value>35000){
      let calcc=(value*0.014)
      setCalcc(calcc.toFixed())
      
    }else{
