import React from "react";
function useInput(opts) {
  const [value, setValue] = React.useState('');
  const [cal,setCalc]=React.useState('')
  if(value<100){
    let cal=(value*0.014)
    setCalc(cal.toFixed)
  }else if(value>100){
    let cal=(value+value)
    setCalc(cal.toFixed)
  }else{
    alert('mars')
  }
  

  
  
  const input = <input value={value}onChange={e => setValue(e.target.value)}{...opts} />

    
  return [value, input,];
}


export default useInput;
