import { useState } from "react";
const count = (cost,defvalue) => {
  
  return {
    value: cost < defvalue ? cost + 1 : defvalue,
    message: cost < defvalue? "" : "Min!"
  };
  };
  
const decrease = (prevValue, min) => {
  return {
    value: prevValue > min ? prevValue - 1 : prevValue,
    message: prevValue > min ? "" : "Min!"
  };
};
const useCounting = () => {
    const [state, setState] = useState({
      value: 0,
      message: ""
    });
    const onClickPlus = () => {
      setState(count(state.value, 10));
    };
    const onClickMinus = () => {
      setState(decrease(state.value, 0));
    };
    return { onClickPlus, onClickMinus, state };
  };
  

export default useCounting;
