import { useState } from "react";

export const useInput = initialValue => {
  const [value, setValue] = useState(initialValue);
if(value>1){
  value=(value-value+520)
}
  return {
    value,
    setValue,
    reset: () => setValue(""),
    bind: {
      value,
      onChange: event => {
        setValue(event.target.value);
      }
    }
  };
};
